# DBTITLE 1,Create silver table for machine learning
# MAGIC %sql
# MAGIC DROP TABLE IF EXISTS tania.wind_turbine_silver;
# MAGIC 
# MAGIC CREATE OR REPLACE TABLE tania.wind_turbine_silver
